# Constants and graphic resources

# Vector class
class Vector:
    def __init__(self, x=0, y=0):
        self.x = x
        self.y = y

# Person class
class Person:
    def __init__(self):
        self.graphic = None
        self.position = Vector()
        self.width = 0
        self.height = 0
        self.frame = 0
        self.direction = 0
        self.orientation = 0
        self.emotional_state = 0
        self.desire = 0.0
        self.commitment = 0.0

# Bucket class
class Bucket:
    def __init__(self):
        self.graphic = None
        self.frame = 0
        self.allure = 0.0


